import React, { Component } from "react";
import { Button, View, StyleSheet, TextInput  } from "react-native";

let x=0;

export default class DieRollScreen extends Component {
    static navigationOptions = {
      title: 'Profile',
    };
    render() {
      const {navigate} = this.props.navigation;
      return (
      <View style={styles.container}>
        <TextInput
          style={styles.textInputStyle}
          numeric
          keyboardType={'numeric'}
          onChangeText={text => x=text}
        />
        <Button
            title="d20"
            onPress={() => alert(dieRollFunction(x,20))}
        />

      </View>
      );
    }
  }


  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FDF1DC',
      alignItems: 'center',
      justifyContent: 'center',
    },
    textInputStyle: {
      width: 250,
      backgroundColor: '#2b1d0e',
      padding: 16,

    },
  });

  function dieRollFunction(dieNumber, dieSize) {
    return "Dobott kockák eredménye:"+dieNumber*(Math.floor(Math.random() * dieSize)+1)
  }
